mapper.select:
 select 
	from ( 
		select 
			, rn 
	      from ( 
	      		select 
					, rownum as rn 
			      from ( 
						  select 
							from tc_flowSub t
						   where 1 = 1

						       and t.tOperatorId = #{tOperatorId,jdbcType=VARCHAR}


						       and t.countryNo = #{countryNo,jdbcType=VARCHAR}


						       and t.mobileNo like #{mobileNo,jdbcType=VARCHAR}||'%'


						       and t.fGName like '%'||#{fGName,jdbcType=VARCHAR}||'%'


						       and t.operatorOrderNo like #{operatorOrderNo,jdbcType=VARCHAR}||'%'


						       and t.status = #{status,jdbcType=VARCHAR}


						       and t.subDate >= #{startDate,jdbcType=TIMESTAMP}


						       and t.subDate  < #{endDate,jdbcType=TIMESTAMP} +1

						    order by subdate
					)
			) 
		  where rn <= ${pager.pageTail} 
	) where rn >= ${pager.pageOffset}

 select count(1)
	from tc_flowSub t
   where 1 = 1

       and t.tOperatorId = #{tOperatorId,jdbcType=VARCHAR}


       and t.countryNo = #{countryNo,jdbcType=VARCHAR}


       and t.mobileNo like #{mobileNo,jdbcType=VARCHAR}||'%'


       and t.fGName like '%'||#{fGName,jdbcType=VARCHAR}||'%'


       and t.operatorOrderNo like #{operatorOrderNo,jdbcType=VARCHAR}||'%'


       and t.status = #{status,jdbcType=VARCHAR}


       and t.subDate >= #{startDate,jdbcType=TIMESTAMP}


       and t.subDate  < #{endDate,jdbcType=TIMESTAMP} +1
