mapper.select:
select 

from HD_PROMOTIONSORT
where MEMBERID = #{memberid,jdbcType=VARCHAR} and PROMOTIONID = #{promotionid,jdbcType=VARCHAR}

select * from (
	select t.*, rownum rn from (
		select * from HD_PROMOTIONSORT 
		where promotionid = #{promotionid,jdbcType=VARCHAR} 
		order by TOPSCORE desc, TOPSCOREDATE asc 
	) t where rownum  <= #{endnum,jdbcType=DECIMAL}
) where rn >= #{startnum,jdbcType=DECIMAL}
